package corejava;

public class Operators 
{
	public void operator()
	{
		int a=10;
		int b=20;
		int c;
	
		System.out.println(c=a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a>b);
		System.out.println(a<b);
		System.out.println(a==b);
		System.out.println(a<=b);
		System.out.println(a>=b);
	}
	

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		Operators op=new Operators();
		op.operator();

	}

}
